/* eslint-disable no-unused-vars */
import React from 'react'
import axios from 'axios'
import './App.css'
import { Route, Routes } from 'react-router'
import Landing from './pages/Landing'

function App () {
  const [formData, setFormData] = React.useState({
    email: '',
    password: ''
  })

  const handleChange = (Event) => {
    setFormData(prevState => {
      return {
        ...prevState,
        [Event.target.name]: Event.target.value
      }
    })
  }

  const handleSubmit = (Event) => {
    Event.preventDefault()
    // console.log(formData)

    axios({
      method: 'post',
      url: '/authorize/passport/',
      data: formData,
      headers: {
        'Content-Type': 'application/json',
        Accept: '*/*'
      }

    })
      .then((data) => {
        if (data) {
          const scopes = 'user-read-currently-playing playlist-modify-public user-library-modify playlist-modify-private playlist-read-collaborative playlist-read-private'
          const authorizeEndpoint = 'https://accounts.spotify.com/authorize?'

          const authObject = {
            response_type: 'code',
            client_id: '87b59a7fa8cf4a0b85c227afc162a118',
            scope: scopes,
            redirect_uri: 'http://localhost:5000/authorize',
            show_dialog: true
          }
          const authQueryString = new URLSearchParams(authObject).toString()
          console.log(authQueryString)
          window.location.href = authQueryString
        }
      })
  }
  return (

  // <Routes>
  //   <Route path='/landing' element={<Landing/>} />
  // </Routes>
        <form onSubmit={handleSubmit}>
          <input
            placeholder='email'
            name='email'
            value={formData.email}
            onChange={handleChange}
          >
          </input>

          <input
            placeholder='password'
            name='password'
            value={formData.password}
            onChange={handleChange}
          >
          </input>
          <button type='submt'>Submit!</button>
        </form>

  )
}

export default App
